mapper.select:

select 

from storage
where goods_id = #{goodsId,jdbcType=INTEGER}
  and supplier_id = #{supplierId,jdbcType=INTEGER}
  and storage_date = #{storageDate,jdbcType=TIMESTAMP}

mapper.delete:

delete from storage
where goods_id = #{goodsId,jdbcType=INTEGER}
  and supplier_id = #{supplierId,jdbcType=INTEGER}
  and storage_date = #{storageDate,jdbcType=TIMESTAMP}

mapper.update:

update storage

  
    storage_amount = #{storageAmount,jdbcType=INTEGER},
  
  
    storage_pay = #{storagePay,jdbcType=REAL},
  

where goods_id = #{goodsId,jdbcType=INTEGER}
  and supplier_id = #{supplierId,jdbcType=INTEGER}
  and storage_date = #{storageDate,jdbcType=TIMESTAMP}


update storage
set storage_amount = #{storageAmount,jdbcType=INTEGER},
  storage_pay = #{storagePay,jdbcType=REAL}
where goods_id = #{goodsId,jdbcType=INTEGER}
  and supplier_id = #{supplierId,jdbcType=INTEGER}
  and storage_date = #{storageDate,jdbcType=TIMESTAMP}
